import { act } from "react-dom/test-utils";
import {SET_ALERT, REMOVE_ALERT} from '../types';

export default(state, action) => {
    switch(action.type){
        case SET_ALERT:
            return action.payload;
        case REMOVE_ALERT:
            return null;
        default:
            return state;
    }
}
